mapper.select:
SELECT
	*
FROM
	SY_LEDGER
WHERE
	USER_NO = #{userNo}

	AND IN_OUT = #{inOut}


	AND CATEGORY = #{category}


	AND ASSET = #{asset}


	AND ADD_DATE BETWEEN #{startDate} AND #{endDate}


	AND AMOUNT BETWEEN #{startAmount} AND #{endAmount}


	AND CONTENT LIKE CONCAT('%', #{keyword}, '%')

ORDER BY
	ADD_DATE ASC
